				vec4 vertex4 = vec4(a_vertex,1.0);
				//position
				vec3 normal = vec3(0.0,1.0,0.0);

				#ifdef USE_VERTEX_SHADER_CODE
					USE_VERTEX_SHADER_CODE
				#endif

				#ifdef USE_MORPHING
					applyMorphing(vertex4, normal);
				#endif

