			varying vec2 coord;
			uniform vec4 color;
			uniform vec2 texSize;
			uniform float time;

			void main() {
				vec3 color;
				float size = 0.1;
				vec2 p = vec2(ivec2(coord * texSize / size)) + 0.5 + 1.0 / size;
				color = floor(((texture2D(texture, p / texSize * size).rgb - 0.5) * 1.6 + 0.8) * 2.0) / 2.0;
				gl_FragColor = vec4(color, 1.0);
			}

